import React, { useContext, useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { Toaster, toast } from 'react-hot-toast';
import Context from '../context/MyContext';
import NomeUsuario from '../components/NomeUsuario';
import '../css/comprarOuVender.css';

export default function ComprarOuVender() {
  const { id } = useParams();
  const navigate = useNavigate();
  const {
    email,
    usuarios,
    acoes,
    infoBotao,
    CVUsuario,
    setCVUsuario,
    CVAcao,
    setCVAcao,
  } = useContext(Context);
  const [valorBotao, setValorBotao] = useState('');
  const [valorInput, setValorInput] = useState('');

  useEffect(() => {
    setValorInput('');
    setValorBotao('');
  }, []);

  const btnConfirmar = () => {
    if (valorBotao === '' || valorInput === '') {
      toast.error('Deve informar a opção');
    }
    if (infoBotao === 'acaoUsuario') {
      usuarios.find((usuario) => usuario.email === email).minhasAcoes
        .filter((user) => user.id === Number(id)).map((acaoId) => (
          setCVUsuario([...CVUsuario, {
            id: acaoId.id,
            nomeAcao: acaoId.nomeAcao,
            preco: acaoId.preco,
            quantidade: (valorInput / acaoId.preco),
            input: valorInput,
            btn: valorBotao,
          }])
        ));
    }
    if (infoBotao === 'acaoDisponivel') {
      acoes.filter((acao) => acao.id === Number(id))
        .map((acaoId) => (
          setCVAcao([...CVAcao, {
            id: acaoId.id,
            nomeAcao: acaoId.nomeAcao,
            preco: acaoId.preco,
            quantidade: acaoId.quantidade,
            input: valorInput,
            btn: valorBotao,
          }])
        ));
    }
  };

  return (
    <div>
      <Toaster />
      <h1>ComprarOuVender</h1>
      <NomeUsuario />
      <h2>Comprar/Vender Ação</h2>
      <table border="1">
        <thead>
          <tr>
            <th>Ação</th>
            <th>Quantidade</th>
            <th>Valor (R$)</th>
          </tr>
        </thead>
        <tbody>
          {
          infoBotao === 'acaoUsuario'
            ? (
              usuarios.find((usuario) => usuario.email === email).minhasAcoes
                .filter((user) => user.id === Number(id)).map((acaoId) => (
                  <tr key={acaoId.id}>
                    <td>{acaoId.nomeAcao}</td>
                    <td>{acaoId.quantidade}</td>
                    <td>{acaoId.preco}</td>
                  </tr>
                ))
            )
            : (
              acoes.filter((acao) => acao.id === Number(id))
                .map((acaoId) => (
                  <tr key={acaoId.id}>
                    <td>{acaoId.nomeAcao}</td>
                    <td>{acaoId.quantidade}</td>
                    <td>{acaoId.preco}</td>
                  </tr>
                ))
            )
          }
        </tbody>
      </table>
      <div>
        <button
          type="button"
          value="btnComprar"
          onClick={({ target: { value } }) => setValorBotao(value)}
        >
          Comprar
        </button>
        <input
          type="text"
          placeholder="Informe o valor"
          onChange={({ target: { value } }) => setValorInput(value)}
        />
      </div>
      <div>
        <button
          type="button"
          value="btnVender"
          onClick={({ target: { value } }) => setValorBotao(value)}
        >
          Vender
        </button>
        <input
          type="text"
          placeholder="Informe o valor"
          onChange={({ target: { value } }) => setValorInput(value)}
        />
      </div>
      <button
        type="button"
        onClick={() => navigate('/lista-acoes')}
      >
        Voltar
      </button>
      <button
        type="button"
        onClick={btnConfirmar}
      >
        Confirmar
      </button>
    </div>
  );
}
