import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function Login() {
  const navigate = useNavigate();
  const [infoLogin, setInfoLogin] = useState({
    email: '',
    senha: '',
  });
  // console.log(infoLogin);

  const redirect = () => {
    navigate('/lista-acoes');
  };

  // const senha = ({ target }) => {
  //   const { value } = target;
  //   setInfoLogin({ senha: value });
  // };

  const getValue = (key, param) => {
    setInfoLogin({ ...infoLogin, [key]: param });
  };

  return (
    <div>
      <h1>Login</h1>
      <input
        type="text"
        name="email"
        placeholder="email"
        onChange={({ target: { value } }) => getValue('email', value)}
      />
      <input
        type="password"
        name="senha"
        placeholder="senha"
        onChange={({ target: { value } }) => getValue('senha', value)}
      />
      <button type="button" onClick={redirect}>
        Acessar
      </button>
    </div>
  );
}
