import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function Login() {
  const navigate = useNavigate();
  const [infoLogin, setInfoLogin] = useState({
    email: '',
    senha: '',
  });
  // console.log(infoLogin);

  const redirect = () => {
    navigate('/lista-acoes');
  };

  const senha = ({ target }) => {
    const { value } = target;
    setInfoLogin({ senha: value });
  };

  return (
    <div>
      <h1>Login</h1>
      <input
        type="text"
        name="email"
        placeholder="email"
        onChange={({ target: { value } }) => setInfoLogin((prevState) => ({
          senha: prevState.senha,
          email: value,
        }))}
      />
      <input
        type="password"
        name="senha"
        placeholder="senha"
        onChange={senha}
      />
      <button type="button" onClick={redirect}>
        Acessar
      </button>
    </div>
  );
}
