import React, { useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import Context from '../context/MyContext';
import NomeUsuario from '../components/NomeUsuario';

export default function ListaDeAcoes() {
  const navigate = useNavigate();
  const { email, usuarios, acoes } = useContext(Context);

  const usuariosFilter = usuarios.find((usuario) => usuario.email === email);
  console.log(usuariosFilter, 'usuariosFilter');

  const usuarioMinhasAcoesId = usuariosFilter.minhasAcoes.map(({ acaoId }) => {
    const teste = {
      acaoId,
    };
    return teste;
  });
  console.log(usuarioMinhasAcoesId, 'usuarioMinhasAcoesId');

  const filterIdsAcoes = acoes
    .filter((acaoId) => acaoId === usuarioMinhasAcoesId
      .map((test) => test.map((abc) => abc.acaoId)));
  console.log(filterIdsAcoes, 'filterIdsAcoes');

  return (
    <div>
      <h1>ListaDeAcoes</h1>
      <NomeUsuario />
      <div>
        <h2>Minhas Ações</h2>
        <table border="1">
          <thead>
            <tr>
              <th>Ação</th>
              <th>Quantidade</th>
              <th>Valor (R$)</th>
              <th>Negociar</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>nomeAcao</td>
              <td>quantidade</td>
              <td>precoAcao</td>
              <td>
                <button type="button">C</button>
                <button type="button">V</button>
              </td>
            </tr>
          </tbody>
        </table>
      </div>
      <div>
        <h2>Disponíveis para investir</h2>
        <table border="1">
          <thead>
            <tr>
              <th>Ação</th>
              <th>Quantidade</th>
              <th>Valor (R$)</th>
              <th>Negociar</th>
            </tr>
          </thead>
          <tbody>
            {
              acoes.map((acao) => (
                <tr key={acao.id}>
                  <td>{acao.nomeAcao}</td>
                  <td>1</td>
                  <td>{acao.preco}</td>
                  <td>
                    <button type="button">C</button>
                    <button type="button">V</button>
                  </td>
                </tr>
              ))
            }
          </tbody>
        </table>
      </div>
      <button
        type="button"
        onClick={() => navigate('/deposito-retirada')}
      >
        Depósito/Retirada
      </button>
    </div>
  );
}
