import React, { useContext } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import Context from '../context/MyContext';
import NomeUsuario from '../components/NomeUsuario';

export default function ComprarOuVender() {
  const navigate = useNavigate();
  const { email, usuarios, acoes } = useContext(Context);
  const { id } = useParams();
  // console.log(value, 'value');

  const userMinhasAcoes = usuarios.find((usuario) => usuario.email === email).minhasAcoes
    .filter((user) => user.id === id).map(({ nomeAcao }) => console.log(nomeAcao, 'nomeAcao'));
  console.log(userMinhasAcoes, 'userMinhasAcoes');

  return (
    <div>
      <h1>ComprarOuVender</h1>
      <NomeUsuario />
      <h2>Comprar/Vender Ação</h2>
      <table border="1">
        <thead>
          <tr>
            <th>Ação</th>
            <th>Quantidade</th>
            <th>Valor (R$)</th>
          </tr>
        </thead>
        <tbody>
          {
            acoes.filter((acao) => acao.id === Number(id))
              .map((aca) => (
                <tr key={aca.id}>
                  <td>{aca.nomeAcao}</td>
                  <td>{aca.quantidade}</td>
                  <td>{aca.preco}</td>
                </tr>
              ))
          }
        </tbody>
      </table>
      <div>
        <button type="button">Comprar</button>
        <input type="text" placeholder="Informe o valor" />
      </div>
      <div>
        <button type="button">Vender</button>
        <input type="text" placeholder="Informe o valor" />
      </div>
      <button
        type="button"
        onClick={() => navigate('/lista-acoes')}
      >
        Voltar
      </button>
      <button type="button">Confirmar</button>
    </div>
  );
}
