import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import NomeUsuario from '../components/NomeUsuario';
import Context from '../context/MyContext';

export default function DepositoOuRetirada() {
  const navigate = useNavigate();
  const { email, usuarios } = useContext(Context);
  const [valor, setValor] = useState(0);
  const [valueBotao, setValueBotao] = useState('');

  const filterConta = usuarios.filter((usuario) => usuario.email === email)
    .map(({ conta }) => conta);

  const botaoDeposito = ({ target: { value } }) => {
    setValueBotao(value);

    return valor;
  };
  console.log(botaoDeposito, 'botaoDeposito');

  const botaoRetirada = ({ target: { value } }) => {
    setValueBotao(value);

    return valor;
  };

  // const botaoConfirmar = () => {
  //   const botao = valueBotao;

  //   if (botao === 'botaoDeposito') {

  //   } if (botao === 'botaoRetirada') {

  //   }
  //   alert('deve selecionar Deposito ou Retirada');

  //   return botaoConfirmar;
  // };

  return (
    <div>
      <h1>DepositoOuRetirada</h1>
      <NomeUsuario />
      <div>
        <p>Saldo em Conta:</p>
        <p>{`R$ ${filterConta}`}</p>
      </div>
      <div>
        <button
          type="button"
          value="botaoDeposito"
          onClick={botaoDeposito}
        >
          Dep√≥sito
        </button>
        <button
          type="button"
          value="botaoRetirada"
          onClick={botaoRetirada}
        >
          Retirada
        </button>
      </div>
      <input
        type="text"
        id="text"
        placeholder="Informe o Valor"
        onChange={({ target: { value } }) => setValor(value)}
      />
      <div>
        <button
          type="button"
          onClick={() => navigate('/lista-acoes')}
        >
          Voltar
        </button>
        <button
          type="button"
          // onClick={botaoConfirmar}
        >
          Confirmar
        </button>
      </div>
    </div>
  );
}
