import React, { useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import Context from '../context/MyContext';
import NomeUsuario from '../components/NomeUsuario';

export default function ListaDeAcoes() {
  const navigate = useNavigate();
  const { email, usuarios, acoes } = useContext(Context);

  // const qtde =  usuarios.filter((usuario) => usuario.email === email)
  // .map(({ minhasAcoes }) => minhasAcoes).

  return (
    <div>
      <h1>ListaDeAcoes</h1>
      <NomeUsuario />
      <div>
        <h2>Minhas Ações</h2>
        <table border="1">
          <thead>
            <tr>
              {/* <th>Ação</th> */}
              <th>Quantidade</th>
              {/* <th>Valor (R$)</th> */}
              <th>Negociar</th>
            </tr>
          </thead>
          <tbody>
            {
              usuarios.filter((usuario) => usuario.email === email)
                .map(({ minhasAcoes }) => minhasAcoes).map(({ quantidade }) => (
                  <tr key={quantidade}>
                    {/* <td>{acoes.nomeAcao}</td> */}
                    <td>{quantidade}</td>
                    {/* <td>{acoes.preco}</td> */}
                    <td>
                      <button type="button">C</button>
                      <button type="button">V</button>
                    </td>
                  </tr>
                ))
            }
          </tbody>
        </table>
      </div>
      <div>
        <h2>Disponíveis para investir</h2>
        <table border="1">
          <thead>
            <tr>
              <th>Ação</th>
              <th>Quantidade</th>
              <th>Valor (R$)</th>
              <th>Negociar</th>
            </tr>
          </thead>
          <tbody>
            {
              acoes.map((acao) => (
                <tr key={acao.id}>
                  <td>{acao.nomeAcao}</td>
                  <td>1</td>
                  <td>{acao.preco}</td>
                  <td>
                    <button type="button">C</button>
                    <button type="button">V</button>
                  </td>
                </tr>
              ))
            }
          </tbody>
        </table>
      </div>
      <button
        type="button"
        onClick={() => navigate('/deposito-retirada')}
      >
        Depósito/Retirada
      </button>
    </div>
  );
}
